Array generationa = linspace(1,11,5);b = 1:2:10;c = zeros(5,6);d = ones(4,3);random_normal_val = randn(5,4); % creates a 5 X 4 matrix with each entry N(0,1);random_uniform_val = rand(5,4); % creates a 5 X 4 matrix with each entry Uniform in (0,1);
For loopx = [1:0.1:10];expx = zeros(1,length(x));disp([size(x),size(expx)]);for i = 1:length(x)    expx(i) = exp(-x(i));endfigure(1);plot(x,expx);xlabel(&#x27;x&#x27;);ylabel(&#x27;e^&#123;-x&#125;&#x27;);grid on;
While loopx = [1:0.01:10];logx = zeros(1,length(x));j = 1;while j &lt;= length(x)    logx(j) = log(x(j));    j = j+1;endfigure(2);plot(x,logx);xlabel(&#x27;x&#x27;);ylabel(&#x27;log(x)&#x27;);grid on;
Vectorizationx = [1:0.01:10];expx = exp(x);logx = log(x);figure(3)plot(x,logx);grid on;
Element wise operationsx = [1:1:10];squarex = x.^2;sumx = x + squarex;disp([x,&#x27;squarex&#x27;,sumx]);mulx = x.*squarex;divx = x./squarex;figure(3)plot(x,divx);
Logical operationsx = [1:1:10];squarex = x.^2;thresholdx = (x &gt; 5);a = x(x &gt; 5);b = squarex((x&gt;2));squarex(x &lt; 3) = 0;
Histogram
histogram(X,nbins)

x = randn(10000,1); %creates 10000 independent standart normal RVsfigure(1);h2 = histogram(x,50,&#x27;Normalization&#x27;,&#x27;pdf&#x27;);x = rand(10000,1); %creates 10000 independent uniform RVs, each unifrom in (0,1);figure(2);h3 = histogram(x,50,&#x27;Normalization&#x27;,&#x27;pdf&#x27;);% hold on;% h2 = histogram(x,200,&#x27;Normalization&#x27;,&#x27;pdf&#x27;);
Stem plotsfigure(3);y = linspace(0,2*pi,50);stem(sin(y));
Regular plotsfigure(4);plot(y,cos(y),y,sin(y),&#x27;Linewidth&#x27;,2);xlabel(&#x27;y&#x27;);ylabel(&#x27;F(y)&#x27;);legend(&#x27;cos(y)&#x27;,&#x27;sin(y)&#x27;);grid on;
Subplotfigure(5);subplot(2,2,1);h2 = histogram(x,50);title(&#x27;Subplot 1: histogram&#x27;);subplot(2,2,2);stem(sin(y));title(&#x27;Subplot 2: sin(y)&#x27;);subplot(2,2,3);plot(y,cos(y),y,sin(y),&#x27;Linewidth&#x27;,2);xlabel(&#x27;y&#x27;);ylabel(&#x27;F(y)&#x27;);legend(&#x27;cos(y)&#x27;,&#x27;sin(y)&#x27;);title(&#x27;Subplot 3: cos(y),sin(y)&#x27;);subplot(2,2,4);stem(exp(y));title(&#x27;Subplot 4: exp(y)&#x27;);
Functionsx = [1:2:10];avgx = average(x);X = create_bernouli(0.2,3,4);%function definitions must appear at the end of the filefunction ave = average(x)    ave = sum(x(:))/numel(x);end
Create Bernoulifunction X = create_bernouli(p,n,m) % n X m matrix with each element bernouli p    U = rand(n,m);    X = (U &lt;= p);end
